import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useProgress } from '../context/ProgressContext';
import './GamePage.css';

const NumberSequencing = () => {
  const navigate = useNavigate();
  const { progress, recordAnswer, updateLevel, addTime } = useProgress();
  const gameProgress = progress.numberSequencing;

  const [numbers, setNumbers] = useState([]);
  const [sortedNumbers, setSortedNumbers] = useState([]);
  const [correctOrder, setCorrectOrder] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [round, setRound] = useState(0);
  const [draggedIndex, setDraggedIndex] = useState(null);
  const [startTime, setStartTime] = useState(Date.now());

  const generateSequence = () => {
    const level = gameProgress.level;
    let count, maxNum;
    if (level <= 2) {
      count = 4;
      maxNum = 10;
    } else if (level <= 4) {
      count = 5;
      maxNum = 15;
    } else {
      count = 6;
      maxNum = 20;
    }
    const nums = [];
    while (nums.length < count) {
      const num = Math.floor(Math.random() * maxNum) + 1;
      if (!nums.includes(num)) {
        nums.push(num);
      }
    }
    const sorted = [...nums].sort((a, b) => a - b);
    const shuffled = [...nums].sort(() => Math.random() - 0.5);
    setCorrectOrder(sorted);
    setNumbers(shuffled);
    setSortedNumbers([]);
    setFeedback('');
  };

  useEffect(() => {
    generateSequence();
    setStartTime(Date.now());
  }, [round, gameProgress.level]);

  const handleDragStart = (index) => {
    setDraggedIndex(index);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDropOnZone = () => {
    if (draggedIndex !== null) {
      const newSorted = [...sortedNumbers, numbers[draggedIndex]];
      setSortedNumbers(newSorted);
      const newNumbers = numbers.filter((_, idx) => idx !== draggedIndex);
      setNumbers(newNumbers);
      setDraggedIndex(null);
    }
  };

  const handleDropOnCard = (dropIndex) => {
    if (draggedIndex !== null && draggedIndex !== dropIndex) {
      const newNumbers = [...numbers];
      [newNumbers[draggedIndex], newNumbers[dropIndex]] = [newNumbers[dropIndex], newNumbers[draggedIndex]];
      setNumbers(newNumbers);
      setDraggedIndex(null);
    }
  };

  const handleCheck = () => {
    const isCorrect = JSON.stringify(sortedNumbers) === JSON.stringify(correctOrder);
    recordAnswer('numberSequencing', isCorrect);
    const timeSpent = Math.floor((Date.now() - startTime) / 1000);
    addTime(timeSpent);
    if (isCorrect) {
      setFeedback('correct');
      if (gameProgress.completed > 0 && (gameProgress.completed + 1) % 5 === 0) {
        const accuracy = ((gameProgress.correct + 1) / (gameProgress.completed + 1)) * 100;
        if (accuracy >= 80 && gameProgress.level < 10) {
          updateLevel('numberSequencing', gameProgress.level + 1);
        }
      }
      setTimeout(() => {
        setRound(round + 1);
      }, 1500);
    } else {
      setFeedback('try-again');
      setTimeout(() => {
        setFeedback('');
      }, 1500);
    }
  };

  const handleReset = () => {
    setNumbers([...numbers, ...sortedNumbers].sort(() => Math.random() - 0.5));
    setSortedNumbers([]);
    setFeedback('');
  };

  return (
    <div className="pastel-background game-page">
      <button className="nav-button back-button" onClick={() => navigate('/dashboard')}>
        ‚Üê Back
      </button>
      <button className="nav-button home-button" onClick={() => navigate('/')}>üè† Home</button>
      <div className="content-container">
        <div className="game-header">
          <h1 className="game-title">Number Sequencing</h1>
          <div className="game-info">
            <span className="level-badge">Level {gameProgress.level}</span>
            <span className="score-badge">Score: {gameProgress.correct}/{gameProgress.completed}</span>
          </div>
        </div>
        <div className="question-prompt">Drag numbers to arrange them from smallest to largest</div>
        <div className="sequence-container">
          <div className="text" style={{ textAlign: 'center', marginBottom: '20px', fontSize: '1.1rem' }}>
            Available Numbers:
          </div>
          <div className="number-cards">
            {numbers.map((num, index) => (
              <div
                key={`num-${num}-${index}`}
                className={`number-card ${draggedIndex === index ? 'dragging' : ''}`}
                draggable
                onDragStart={() => handleDragStart(index)}
                onDragOver={handleDragOver}
                onDrop={() => handleDropOnCard(index)}
              >
                {num}
              </div>
            ))}
          </div>
          <div className="text" style={{ textAlign: 'center', margin: '30px 0 15px', fontSize: '1.1rem' }}>
            Sorted Order (Drag here):
          </div>
          <div
            className={`drop-zone ${draggedIndex !== null ? 'drag-over' : ''}`}
            onDragOver={handleDragOver}
            onDrop={handleDropOnZone}
          >
            {sortedNumbers.length === 0 ? (
              <div className="text" style={{ color: '#A5C8FF' }}>
                Drop numbers here in order
              </div>
            ) : (
              sortedNumbers.map((num, index) => (
                <div key={`sorted-${num}-${index}`} className="number-card">
                  {num}
                </div>
              ))
            )}
          </div>
          <div className="answer-buttons" style={{ marginTop: '30px' }}>
            <button
              className="btn"
              onClick={handleCheck}
              disabled={sortedNumbers.length !== correctOrder.length}
            >
              Check Answer
            </button>
            <button className="btn btn-secondary" onClick={handleReset}>
              Reset
            </button>
          </div>
        </div>
        {feedback && (
          <div className={`feedback ${feedback}`}>
            {feedback === 'correct' ? (
              <>
                <span className="feedback-icon">‚úì</span>
                <span>Perfect order!</span>
              </>
            ) : (
              <>
                <span className="feedback-icon">‚Üª</span>
                <span>Try again</span>
              </>
            )}
          </div>
        )}
        <div className="progress-indicator">
          <div className="progress-text">
            Round {gameProgress.completed + 1} ‚Ä¢ Level {gameProgress.level}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NumberSequencing;
