import React, { createContext, useContext, useState, useEffect } from 'react';

const ProgressContext = createContext();

export const useProgress = () => {
  const context = useContext(ProgressContext);
  if (!context) {
    throw new Error('useProgress must be used within a ProgressProvider');
  }
  return context;
};

export const ProgressProvider = ({ children }) => {
  // Initialize from localStorage or use defaults
  const [progress, setProgress] = useState(() => {
    const saved = localStorage.getItem('mathverse-progress');
    return saved ? JSON.parse(saved) : {
      quantityComparison: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      numberLineAddition: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      patternRecognition: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      numberSequencing: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      totalTime: 0, // in seconds
      sessions: 0,
      lastSession: null
    };
  });

  const [settings, setSettings] = useState(() => {
    const saved = localStorage.getItem('mathverse-settings');
    return saved ? JSON.parse(saved) : {
      soundEnabled: false,
      animationSpeed: 'normal', // slow, normal, fast
      showTimer: false,
      difficulty: 'adaptive', // easy, medium, hard, adaptive
      theme: 'pastel' // pastel, dark-mode
    };
  });

  // Save to localStorage whenever progress changes
  useEffect(() => {
    localStorage.setItem('mathverse-progress', JSON.stringify(progress));
  }, [progress]);

  useEffect(() => {
    localStorage.setItem('mathverse-settings', JSON.stringify(settings));
    // Apply theme to body
    document.body.className = settings.theme || 'pastel';
  }, [settings]);

  const updateProgress = (game, data) => {
    setProgress(prev => ({
      ...prev,
      [game]: {
        ...prev[game],
        ...data
      }
    }));
  };

  const recordAnswer = (game, isCorrect) => {
    setProgress(prev => ({
      ...prev,
      [game]: {
        ...prev[game],
        completed: prev[game].completed + 1,
        correct: prev[game].correct + (isCorrect ? 1 : 0),
        incorrect: prev[game].incorrect + (isCorrect ? 0 : 1)
      }
    }));
  };

  const updateLevel = (game, newLevel) => {
    setProgress(prev => ({
      ...prev,
      [game]: {
        ...prev[game],
        level: newLevel
      }
    }));
  };

  const startSession = () => {
    setProgress(prev => ({
      ...prev,
      sessions: prev.sessions + 1,
      lastSession: new Date().toISOString()
    }));
  };

  const addTime = (seconds) => {
    setProgress(prev => ({
      ...prev,
      totalTime: prev.totalTime + seconds
    }));
  };

  const updateSettings = (newSettings) => {
    setSettings(prev => ({
      ...prev,
      ...newSettings
    }));
  };

  const resetProgress = () => {
    const defaultProgress = {
      quantityComparison: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      numberLineAddition: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      patternRecognition: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      numberSequencing: { completed: 0, correct: 0, incorrect: 0, level: 1 },
      totalTime: 0,
      sessions: 0,
      lastSession: null
    };
    setProgress(defaultProgress);
    localStorage.setItem('mathverse-progress', JSON.stringify(defaultProgress));
  };

  const value = {
    progress,
    settings,
    updateProgress,
    recordAnswer,
    updateLevel,
    startSession,
    addTime,
    updateSettings,
    resetProgress
  };

  return (
    <ProgressContext.Provider value={value}>
      {children}
    </ProgressContext.Provider>
  );
};
