import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useProgress } from '../context/ProgressContext';
import './GamePage.css';
import './QuantityComparison.css';

const correctSound = new Audio('/correct.mp3');
const wrongSound = new Audio('/wrong.mp3');

const QuantityComparison = () => {
  const navigate = useNavigate();
  const { progress, recordAnswer, updateLevel, addTime, settings } = useProgress();
  const gameProgress = progress.quantityComparison;

  const [leftCount, setLeftCount] = useState(0);
  const [rightCount, setRightCount] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [round, setRound] = useState(0);
  const [startTime, setStartTime] = useState(Date.now());
  const [timeElapsed, setTimeElapsed] = useState(0);

  // Sound Effect Helper
  const playSound = (isCorrect) => {
    if (settings?.soundEnabled) {
      const audio = isCorrect ? correctSound : wrongSound;
      audio.currentTime = 0;
      audio.play().catch(e => console.log("Audio play failed:", e));
    }
  };

  // Timer Effect
  useEffect(() => {
    let timer;
    if (settings?.showTimer && !feedback) {
      timer = setInterval(() => {
        setTimeElapsed(Math.floor((Date.now() - startTime) / 1000));
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [settings?.showTimer, startTime, feedback]);

  // Generate new question based on level and difficulty
  const generateQuestion = () => {
    let level = gameProgress.level;

    // Adjust based on settings
    if (settings?.difficulty === 'easy') level = Math.max(1, level - 1);
    else if (settings?.difficulty === 'hard') level = Math.min(10, level + 1);

    let maxCount;
    if (level <= 2) maxCount = 3;
    else if (level <= 4) maxCount = 5;
    else if (level <= 6) maxCount = 7;
    else maxCount = 10;

    const left = Math.floor(Math.random() * maxCount) + 1;
    let right;
    do {
      right = Math.floor(Math.random() * maxCount) + 1;
    } while (
      (level <= 2 && Math.abs(left - right) < 1) ||
      (level > 2 && level <= 4 && Math.abs(left - right) === 0)
    );
    setLeftCount(left);
    setRightCount(right);
    setSelectedAnswer(null);
    setFeedback('');
    setTimeElapsed(0);
  };

  useEffect(() => {
    generateQuestion();
    setStartTime(Date.now());
  }, [round, gameProgress.level, settings?.difficulty]);

  const handleAnswer = (answer) => {
    if (selectedAnswer) return;
    setSelectedAnswer(answer);

    let correctAnswer;
    if (leftCount > rightCount) correctAnswer = 'more';
    else if (leftCount < rightCount) correctAnswer = 'less';
    else correctAnswer = 'same';

    const isCorrect = answer === correctAnswer;
    playSound(isCorrect);

    recordAnswer('quantityComparison', isCorrect);
    const timeSpent = Math.floor((Date.now() - startTime) / 1000);
    addTime(timeSpent);

    if (isCorrect) {
      setFeedback('correct');
      if (gameProgress.completed > 0 && (gameProgress.completed + 1) % 5 === 0) {
        const accuracy = ((gameProgress.correct + 1) / (gameProgress.completed + 1)) * 100;
        if (accuracy >= 80 && gameProgress.level < 10) {
          updateLevel('quantityComparison', gameProgress.level + 1);
        }
      }
      setTimeout(() => {
        setRound(round + 1);
      }, 1500);
    } else {
      setFeedback('try-again');
      setTimeout(() => {
        setSelectedAnswer(null);
        setFeedback('');
      }, 1500);
    }
  };

  const renderObjects = (count) => {
    const objects = [];
    const animSpeed = settings?.animationSpeed === 'slow' ? 1.5 : settings?.animationSpeed === 'fast' ? 0.4 : 0.8;
    for (let i = 0; i < count; i++) {
      objects.push(
        <div
          key={i}
          className="object apple"
          style={{
            animationDelay: `${i * 0.1}s`,
            animationDuration: `${animSpeed}s`
          }}
        >
          üçé
        </div>
      );
    }
    return objects;
  };

  return (
    <div className={`pastel-background game-page ${settings?.theme === 'dark-mode' ? 'dark-mode' : ''}`}>
      <button className="nav-button back-button" onClick={() => navigate('/dashboard')}>
        ‚Üê Back
      </button>
      <button className="nav-button home-button" onClick={() => navigate('/')}>üè† Home</button>

      {settings?.showTimer && (
        <div className="timer-display">
          ‚è±Ô∏è {timeElapsed}s
        </div>
      )}

      <div className="content-container">
        <div className="game-header">
          <h1 className="game-title">Quantity Comparison</h1>
          <div className="game-info">
            <span className="level-badge">Level {gameProgress.level}</span>
            <span className="score-badge">Score: {gameProgress.correct}/{gameProgress.completed}</span>
          </div>
        </div>
        <div className="question-prompt">Which group has more apples?</div>
        <div className="comparison-container">
          <div className={`object-group left ${selectedAnswer && leftCount > rightCount ? 'highlight-correct' : ''}`}>
            {renderObjects(leftCount)}
          </div>
          <div className="vs-divider"><span>VS</span></div>
          <div className={`object-group right ${selectedAnswer && rightCount > leftCount ? 'highlight-correct' : ''}`}>
            {renderObjects(rightCount)}
          </div>
        </div>
        <div className="answer-buttons">
          <button
            className={`answer-btn ${selectedAnswer === 'more' ? (feedback === 'correct' ? 'correct' : 'incorrect') : ''}`}
            onClick={() => handleAnswer('more')}
            disabled={selectedAnswer !== null}
          >
            Left has More
          </button>
          <button
            className={`answer-btn ${selectedAnswer === 'less' ? (feedback === 'correct' ? 'correct' : 'incorrect') : ''}`}
            onClick={() => handleAnswer('less')}
            disabled={selectedAnswer !== null}
          >
            Right has More
          </button>
          <button
            className={`answer-btn ${selectedAnswer === 'same' ? (feedback === 'correct' ? 'correct' : 'incorrect') : ''}`}
            onClick={() => handleAnswer('same')}
            disabled={selectedAnswer !== null}
          >
            Same Amount
          </button>
        </div>
        {feedback && (
          <div className={`feedback ${feedback}`}>
            {feedback === 'correct' ? (
              <>
                <span className="feedback-icon">‚úì</span>
                <span>Well done!</span>
              </>
            ) : (
              <>
                <span className="feedback-icon">‚Üª</span>
                <span>Try again</span>
              </>
            )}
          </div>
        )}
        <div className="progress-indicator">
          <div className="progress-text">
            Round {gameProgress.completed + 1} ‚Ä¢ Level {gameProgress.level}
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuantityComparison;
