import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useProgress } from '../context/ProgressContext';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './ParentDashboard.css';

const ParentDashboard = () => {
  const navigate = useNavigate();
  const { progress, resetProgress } = useProgress();

  const games = [
    { id: 'quantityComparison', name: 'Quantity Comparison', color: '#FFE5A5' },
    { id: 'numberLineAddition', name: 'Number Line Addition', color: '#A5C8FF' },
    { id: 'patternRecognition', name: 'Pattern Recognition', color: '#D4A5FF' },
    { id: 'numberSequencing', name: 'Number Sequencing', color: '#FFD4A5' }
  ];

  const getAccuracy = (gameId) => {
    const game = progress[gameId];
    if (!game || game.completed === 0) return 0;
    return Math.round((game.correct / game.completed) * 100);
  };

  const getTotalCompleted = () => {
    return games.reduce((sum, game) => sum + (progress[game.id]?.completed || 0), 0);
  };

  const getTotalCorrect = () => {
    return games.reduce((sum, game) => sum + (progress[game.id]?.correct || 0), 0);
  };

  const getOverallAccuracy = () => {
    const total = getTotalCompleted();
    if (total === 0) return 0;
    return Math.round((getTotalCorrect() / total) * 100);
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    if (minutes < 60) return `${minutes}min`;
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours}h ${remainingMinutes}min`;
  };

  // Data for charts
  const accuracyData = games.map(game => ({
    name: game.name.split(' ')[0],
    accuracy: getAccuracy(game.id)
  }));

  const progressData = games.map(game => ({
    name: game.name.split(' ')[0],
    completed: progress[game.id]?.completed || 0,
    correct: progress[game.id]?.correct || 0,
    incorrect: progress[game.id]?.incorrect || 0
  }));

  const handleReset = () => {
    if (window.confirm('Are you sure you want to reset all progress? This cannot be undone.')) {
      resetProgress();
    }
  };

  return (
    <div className="pastel-background parent-dashboard-page">
      <button className="nav-button back-button" onClick={() => navigate('/')}>‚Üê Home</button>
      <div className="content-container">
        <h1 className="heading">Learning Progress Dashboard</h1>
        {/* Summary Cards */}
        <div className="summary-cards">
          <div className="summary-card">
            <div className="summary-icon">üìä</div>
            <div className="summary-value">{getTotalCompleted()}</div>
            <div className="summary-label">Total Rounds</div>
          </div>
          <div className="summary-card">
            <div className="summary-icon">‚úì</div>
            <div className="summary-value">{getOverallAccuracy()}%</div>
            <div className="summary-label">Overall Accuracy</div>
          </div>
          <div className="summary-card">
            <div className="summary-icon">‚è±Ô∏è</div>
            <div className="summary-value">{formatTime(progress.totalTime)}</div>
            <div className="summary-label">Time Spent</div>
          </div>
          <div className="summary-card">
            <div className="summary-icon">üéÆ</div>
            <div className="summary-value">{progress.sessions}</div>
            <div className="summary-label">Sessions</div>
          </div>
        </div>
        {/* Game Details */}
        <div className="game-details-section">
          <h2 className="subheading">Game Details</h2>
          <div className="game-details-grid">
            {games.map((game) => {
              const gameData = progress[game.id];
              return (
                <div key={game.id} className="game-detail-card" style={{ borderColor: game.color }}>
                  <h3 style={{ color: '#5A7BA6' }}>{game.name}</h3>
                  <div className="game-detail-stats">
                    <div className="stat-row">
                      <span className="stat-label">Level:</span>
                      <span className="stat-value">Level {gameData?.level || 1}</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">Completed:</span>
                      <span className="stat-value">{gameData?.completed || 0} rounds</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">Correct:</span>
                      <span className="stat-value" style={{ color: '#4CAF50' }}>{gameData?.correct || 0}</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">Incorrect:</span>
                      <span className="stat-value" style={{ color: '#FF6B6B' }}>{gameData?.incorrect || 0}</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">Accuracy:</span>
                      <span className="stat-value" style={{ fontSize: '1.5rem', fontWeight: '800', color: game.color }}>{getAccuracy(game.id)}%</span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
        {/* Charts */}
        <div className="charts-section">
          <h2 className="subheading">Visual Analytics</h2>
          <div className="chart-container">
            <h3 className="chart-title">Accuracy by Game</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={accuracyData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#E0E0E0" />
                <XAxis dataKey="name" stroke="#7A9BC6" />
                <YAxis stroke="#7A9BC6" domain={[0, 100]} />
                <Tooltip />
                <Bar dataKey="accuracy" fill="#A5C8FF" radius={[10, 10, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>
          <div className="chart-container">
            <h3 className="chart-title">Progress by Game</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={progressData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#E0E0E0" />
                <XAxis dataKey="name" stroke="#7A9BC6" />
                <YAxis stroke="#7A9BC6" />
                <Tooltip />
                <Legend />
                <Bar dataKey="correct" fill="#4CAF50" radius={[10, 10, 0, 0]} />
                <Bar dataKey="incorrect" fill="#FFA5A5" radius={[10, 10, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
        {/* Actions */}
        <div className="dashboard-actions">
          <button className="btn" onClick={() => navigate('/dashboard')}>Continue Learning</button>
          <button className="btn btn-secondary" onClick={() => navigate('/settings')}>Settings</button>
          <button className="btn" onClick={handleReset} style={{ background: 'linear-gradient(135deg, #FFA5A5, #FF6B6B)' }}>Reset Progress</button>
        </div>
      </div>
    </div>
  );
};

export default ParentDashboard;
