import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useProgress } from '../context/ProgressContext';
import './GamePage.css';

const NumberLineAddition = () => {
  const navigate = useNavigate();
  const { progress, recordAnswer, updateLevel, addTime } = useProgress();
  const gameProgress = progress.numberLineAddition;

  const [firstNumber, setFirstNumber] = useState(0);
  const [addNumber, setAddNumber] = useState(0);
  const [currentPosition, setCurrentPosition] = useState(0);
  const [animationStep, setAnimationStep] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [feedback, setFeedback] = useState('');
  const [showInput, setShowInput] = useState(false);
  const [round, setRound] = useState(0);
  const [startTime, setStartTime] = useState(Date.now());

  const maxNumber = 10;

  const generateQuestion = () => {
    const level = gameProgress.level;
    let max, maxAdd;
    if (level <= 2) {
      max = 5;
      maxAdd = 2;
    } else if (level <= 4) {
      max = 7;
      maxAdd = 3;
    } else {
      max = 8;
      maxAdd = 4;
    }
    const first = Math.floor(Math.random() * max) + 1;
    const add = Math.floor(Math.random() * maxAdd) + 1;
    if (first + add <= maxNumber) {
      setFirstNumber(first);
      setAddNumber(add);
      setCurrentPosition(first);
      setAnimationStep(0);
      setShowInput(false);
      setUserAnswer('');
      setFeedback('');
    } else {
      generateQuestion();
    }
  };

  useEffect(() => {
    generateQuestion();
    setStartTime(Date.now());
  }, [round, gameProgress.level]);

  useEffect(() => {
    if (animationStep > 0 && animationStep <= addNumber) {
      const timer = setTimeout(() => {
        setCurrentPosition(firstNumber + animationStep);
        setAnimationStep(animationStep + 1);
      }, 800);
      return () => clearTimeout(timer);
    } else if (animationStep > addNumber) {
      setShowInput(true);
    }
  }, [animationStep]);

  const startAnimation = () => {
    setAnimationStep(1);
  };

  const handleSubmit = () => {
    const answer = parseInt(userAnswer);
    const correct = firstNumber + addNumber;
    const isCorrect = answer === correct;
    recordAnswer('numberLineAddition', isCorrect);
    const timeSpent = Math.floor((Date.now() - startTime) / 1000);
    addTime(timeSpent);
    if (isCorrect) {
      setFeedback('correct');
      if (gameProgress.completed > 0 && (gameProgress.completed + 1) % 5 === 0) {
        const accuracy = ((gameProgress.correct + 1) / (gameProgress.completed + 1)) * 100;
        if (accuracy >= 80 && gameProgress.level < 10) {
          updateLevel('numberLineAddition', gameProgress.level + 1);
        }
      }
      setTimeout(() => {
        setRound(round + 1);
      }, 1500);
    } else {
      setFeedback('try-again');
      setTimeout(() => {
        setUserAnswer('');
        setFeedback('');
      }, 1500);
    }
  };

  const renderNumberLine = () => {
    const points = [];
    for (let i = 0; i <= maxNumber; i++) {
      let className = 'number-point';
      if (i === firstNumber && animationStep === 0) className += ' start';
      if (i === currentPosition && animationStep > 0) className += ' current';
      if (i === firstNumber + addNumber && showInput) className += ' end';
      points.push(
        <div key={i} className={className}>
          <div className="number-value">{i}</div>
        </div>
      );
    }
    return points;
  };

  return (
    <div className="pastel-background game-page">
      <button className="nav-button back-button" onClick={() => navigate('/dashboard')}>
        ‚Üê Back
      </button>
      <button className="nav-button home-button" onClick={() => navigate('/')}>üè† Home</button>
      <div className="content-container">
        <div className="game-header">
          <h1 className="game-title">Number Line Addition</h1>
          <div className="game-info">
            <span className="level-badge">Level {gameProgress.level}</span>
            <span className="score-badge">Score: {gameProgress.correct}/{gameProgress.completed}</span>
          </div>
        </div>
        <div className="question-prompt">{firstNumber} + {addNumber} = ?</div>
        <div className="number-line-container">
          <div className="number-line">{renderNumberLine()}</div>
          <div style={{ textAlign: 'center', marginTop: '30px' }}>
            {animationStep === 0 ? (
              <button className="btn" onClick={startAnimation}>Start Moving ‚Üí</button>
            ) : !showInput ? (
              <div className="text" style={{ fontSize: '1.3rem', color: '#7A9BC6' }}>
                Moving forward by {addNumber}...
              </div>
            ) : (
              <div className="number-input-container">
                <p className="text" style={{ marginBottom: '15px', fontSize: '1.2rem' }}>
                  Where did we reach?
                </p>
                <input
                  type="number"
                  className="number-input"
                  value={userAnswer}
                  onChange={(e) => setUserAnswer(e.target.value)}
                  placeholder="?"
                  autoFocus
                />
                <br />
                <button
                  className="btn"
                  onClick={handleSubmit}
                  disabled={!userAnswer}
                  style={{ marginTop: '20px' }}
                >
                  Submit Answer
                </button>
              </div>
            )}
          </div>
        </div>
        {feedback && (
          <div className={`feedback ${feedback}`}>
            {feedback === 'correct' ? (
              <>
                <span className="feedback-icon">‚úì</span>
                <span>Well done!</span>
              </>
            ) : (
              <>
                <span className="feedback-icon">‚Üª</span>
                <span>Try again</span>
              </>
            )}
          </div>
        )}
        <div className="progress-indicator">
          <div className="progress-text">
            Round {gameProgress.completed + 1} ‚Ä¢ Level {gameProgress.level}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NumberLineAddition;
