import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useProgress } from '../context/ProgressContext';
import './GamePage.css';
import './PatternRecognition.css';


const PatternRecognition = () => {
  const navigate = useNavigate();
  const { progress, recordAnswer, updateLevel, addTime } = useProgress();
  const [pattern, setPattern] = useState([]);
  const [options, setOptions] = useState([]);
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [feedback, setFeedback] = useState('');
  const [round, setRound] = useState(0);
  const [startTime, setStartTime] = useState(Date.now());


  const gameProgress = progress.patternRecognition;

  // If patternRecognition progress is missing, initialize it using updateProgress
  useEffect(() => {
    if (!progress.patternRecognition) {
      // Use the context's updateProgress function to safely set it
      if (typeof updateProgress === 'function') {
        updateProgress('patternRecognition', { completed: 0, correct: 0, incorrect: 0, level: 1 });
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [progress.patternRecognition]);

  if (!gameProgress) {
    return (
      <div className="pastel-background game-page">
        <div className="content-container">
          <h1 className="game-title">Pattern Recognition</h1>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  const generatePattern = useCallback(() => {
    if (!gameProgress) return;
    const shapes = ['üî¥', 'üîµ', 'üü°', 'üü¢', 'üü£', 'üü†'];
    const level = gameProgress.level;
    let patternLength, numShapes;
    if (level <= 2) {
      patternLength = 4;
      numShapes = 2;
    } else if (level <= 4) {
      patternLength = 6;
      numShapes = 3;
    } else {
      patternLength = 8;
      numShapes = 3;
    }
    const availableShapes = shapes.slice(0, numShapes);
    let firstShape = availableShapes[Math.floor(Math.random() * availableShapes.length)];
    let secondShape = availableShapes.length > 1 ? availableShapes.find(s => s !== firstShape) : firstShape;
    const unit = [firstShape, secondShape];
    const newPattern = [];
    for (let i = 0; i < patternLength - 1; i++) {
      newPattern.push(unit[i % unit.length]);
    }
    const correct = unit[(patternLength - 1) % unit.length];
    const opts = [correct];
    while (opts.length < 3) {
      const random = availableShapes[Math.floor(Math.random() * availableShapes.length)];
      if (!opts.includes(random)) {
        opts.push(random);
      }
    }
    opts.sort(() => Math.random() - 0.5);
    setPattern(newPattern);
    setCorrectAnswer(correct);
    setOptions(opts);
    setSelectedAnswer('');
    setFeedback('');
  }, [gameProgress]);

  useEffect(() => {
    if (!gameProgress) return;
    generatePattern();
    setStartTime(Date.now());
  }, [round, gameProgress?.level, generatePattern]);

  if (!gameProgress) {
    return (
      <div className="pastel-background game-page">
        <div className="content-container">
          <h1 className="game-title">Pattern Recognition</h1>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  const handleAnswer = (answer) => {
    if (selectedAnswer) return;
    setSelectedAnswer(answer);
    const isCorrect = answer === correctAnswer;
    recordAnswer('patternRecognition', isCorrect);
    const timeSpent = Math.floor((Date.now() - startTime) / 1000);
    addTime(timeSpent);
    if (isCorrect) {
      setFeedback('correct');
      if (gameProgress.completed > 0 && (gameProgress.completed + 1) % 5 === 0) {
        const accuracy = ((gameProgress.correct + 1) / (gameProgress.completed + 1)) * 100;
        if (accuracy >= 80 && gameProgress.level < 10) {
          updateLevel('patternRecognition', gameProgress.level + 1);
        }
      }
      setTimeout(() => {
        setRound(round + 1);
      }, 1500);
    } else {
      setFeedback('try-again');
      setTimeout(() => {
        setSelectedAnswer('');
        setFeedback('');
      }, 1500);
    }
  };

  return (
    <div className="pastel-background game-page">
      <button className="nav-button back-button" onClick={() => navigate('/dashboard')}>
        ‚Üê Back
      </button>
      <button className="nav-button home-button" onClick={() => navigate('/')}>üè† Home</button>
      <div className="content-container">
        <div className="game-header">
          <h1 className="game-title">Pattern Recognition</h1>
          <div className="game-info">
            <span className="level-badge">Level {gameProgress.level}</span>
            <span className="score-badge">Score: {gameProgress.correct}/{gameProgress.completed}</span>
          </div>
        </div>
        <div className="question-prompt">What comes next in the pattern?</div>
        <div className="pattern-container">
          <div className="pattern-display">
            {pattern.map((shape, index) => (
              <div key={index} className="pattern-item" style={{ animationDelay: `${index * 0.1}s` }}>
                {shape}
              </div>
            ))}
            <div className="pattern-item empty">?</div>
          </div>
          <div className="pattern-options">
            {options.map((option, index) => (
              <div
                key={index}
                className={`pattern-option ${selectedAnswer === option ? 'selected' : ''}`}
                onClick={() => handleAnswer(option)}
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                {option}
              </div>
            ))}
          </div>
        </div>
        {feedback && (
          <div className={`feedback ${feedback}`}>
            {feedback === 'correct' ? (
              <>
                <span className="feedback-icon">‚úì</span>
                <span>Well done!</span>
              </>
            ) : (
              <>
                <span className="feedback-icon">‚Üª</span>
                <span>Try again</span>
              </>
            )}
          </div>
        )}
        <div className="progress-indicator">
          <div className="progress-text">
            Round {gameProgress.completed + 1} ‚Ä¢ Level {gameProgress.level}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PatternRecognition;
